  useEffect(()=> {

    const fetchUsers = async () =>{
      try{
        const response = require('./src/components/UserList/user.json');
        console.log(response);
        setUser(response);

      }catch(error){

      }
    };
    fetchUsers();
  },[]);

  return (
    <SafeAreaView>
      {/* <UserList users={user} />  */} 
    <LoginScreen />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFF',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

